mapper.select:
SELECT count(*) as cnt
FROM boards
WHERE board_content LIKE concat('%', '${search}', '%') AND campsite_code LIKE concat('%', '${campsiteCode}' ,'%');

SELECT campsite_code as campsiteCode, count(*) as boardCnt
FROM boards
GROUP BY campsite_code;

mapper.update:
UPDATE boards
SET board_content='${boardContent}', campsite_code='${campsiteCode}', update_date=now()
WHERE board_id = '${boardId}';
